import React from 'react';
export const useSyncWithLS = ({ dataSourceName = 'sync-tabs', callback, uniqueKey, disableActiveTabCallback = false, }) => {
    const key = `${dataSourceName}.${uniqueKey}`;
    const handler = (event) => {
        if (event.key === key && event.newValue) {
            return callback();
        }
        return undefined;
    };
    React.useEffect(() => {
        // Action in non-active tab
        window.addEventListener('storage', handler);
        return () => {
            window.removeEventListener('storage', handler);
            localStorage.removeItem(key);
        };
    });
    return {
        callback: React.useCallback(() => {
            localStorage.setItem(key, String(Number(localStorage.getItem(key) || '0') + 1));
            if (disableActiveTabCallback) {
                return undefined;
            }
            return callback();
        }, [key, disableActiveTabCallback, callback]),
    };
};
